var grantMap = {};

var permit = false;
var context = $evaluation.getContext();
var identity = context.getIdentity();
var attributes = identity.getAttributes();
if (identity.hasRole('admin')) {
    permit = true;
} else {
    var policy = $evaluation.getPolicy();
    var userName = attributes.getValue("preferred_username").asString(0);

    print("User Name " + userName);

    if (userName === policy.getName()) {
        var resourcePermission = $evaluation.getPermission();
        var resourceName = resourcePermission.getResource().getName();
        print("Resource name " + resourceName);
        // Grant access only if resourceName and parentPolicy name match 'grantMap' above
        if (grantMap.hasOwnProperty(resourceName)) {
            var parentPolicy = $evaluation.getParentPolicy();
            if (parentPolicy) {
                print("Parent Policy name " + parentPolicy.getName());
                grantMap[resourceName].forEach(function (s) {
                    if (s === parentPolicy.getName()) {
                        permit = true;
                    }
                });
            }
        }

    }
}

if (permit) {
    $evaluation.grant();
}
